# 1. Типы файлов
DROP TABLE IF EXISTS file_types;
CREATE TABLE file_types(
	id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
	type VARCHAR(20) NOT NULL UNIQUE -- Тип файла
);

INSERT INTO `file_types` VALUES (1,' photo'),(2,' cover'),(3,'video');

# 2. Список файлов
DROP TABLE IF EXISTS files;
CREATE TABLE files(
	id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
	type_id BIGINT UNSIGNED NOT NULL, -- Тип файла
	filename VARCHAR(100) NOT NULL UNIQUE, -- Имя файла
	created_at DATETIME DEFAULT NOW(),  -- Дата и время создания
	updated_at DATETIME ON UPDATE NOW(),  -- Дата и время обновления данных
	size BIGINT UNSIGNED NOT NULL,  -- Размер файла
	FOREIGN KEY (type_id) REFERENCES file_types(id)
);

