# 3. Пользователи
DROP TABLE IF EXISTS users;
CREATE TABLE users(
	id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
	firstname VARCHAR(50) NOT NULL,  -- Имя
	lastname VARCHAR(50),  -- Фамилия
	email VARCHAR(120) UNIQUE,  -- Электронная почта
	password_hash VARCHAR(50),  -- Хэш пароля
	phone BIGINT UNSIGNED UNIQUE,  -- Номер телефона
	gender CHAR(1),  -- Пол
	birthday DATE,  -- Дата рождения
	photo_file_id BIGINT UNSIGNED UNIQUE,  -- Имя файла фото
	created_at DATETIME DEFAULT NOW(),  -- Дата и время создания
	country VARCHAR(50),  -- Страна
	city VARCHAR(50),  -- Город
