# 4. Друзья
DROP TABLE IF EXISTS friend_requests;
CREATE TABLE friend_requests(
	initiator_user_id BIGINT UNSIGNED NOT NULL,  -- id инициатора запроса в друзья
	target_user_id BIGINT UNSIGNED NOT NULL,  -- id пользователя кому направили запрос в друзья
	status ENUM('requested', 'approved', 'declined', 'unfriended'),  -- Статус заявки в друзья
	requested_at DATETIME DEFAULT NOW(),  -- Дата и время создании заявки в друзья
	updated_at DATETIME ON UPDATE NOW(),  -- Дата и время обновления заявки в друзья
	PRIMARY KEY (initiator_user_id, target_user_id),
    FOREIGN KEY (initiator_user_id) REFERENCES users(id),
    FOREIGN KEY (target_user_id) REFERENCES users(id)
	
);

