	film_file_id BIGINT UNSIGNED NOT NULL UNIQUE,  -- Ссылка на файл фильма
	cover_file_id BIGINT UNSIGNED NOT NULL UNIQUE,  -- Ссылка на файл обложки
	FOREIGN KEY (film_file_id) REFERENCES files(id),
	FOREIGN KEY (cover_file_id) REFERENCES files(id)
);

