# 10. Серии
DROP TABLE IF EXISTS episodes_serials;
CREATE TABLE episodes_serials(
	id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
	serial_id BIGINT UNSIGNED NOT NULL,  -- id сериала к которому относится данный сезон
	season_id BIGINT UNSIGNED NOT NULL,  -- id сезона к которому относится данная серия
	number TINYINT UNSIGNED NOT NULL,  -- Номер серии
	name VARCHAR(100),  -- Название серии
	duration TINYINT UNSIGNED NOT NULL,  -- Продолжительность в минутах
	description TEXT,  -- Описание
	episode_file_id BIGINT UNSIGNED NOT NULL UNIQUE,  -- Ссылка на файл эпизода
	cover_file_id BIGINT UNSIGNED NOT NULL,  -- Ссылка на файл обложки
	FOREIGN KEY (episode_file_id) REFERENCES files(id),
	FOREIGN KEY (cover_file_id) REFERENCES files(id),
	FOREIGN KEY (serial_id) REFERENCES serials(id),
	FOREIGN KEY (season_id) REFERENCES seasons_serials(id)
);

