/* Тригер проверки расширения файла при загрузке в талицу с файлами "files" */
DROP TRIGGER IF EXISTS check_file_extension;
DELIMITER $$
CREATE TRIGGER check_file_extension BEFORE INSERT ON files
FOR EACH ROW 
BEGIN
	CASE
		WHEN SUBSTRING(NEW.filename, -4) = '.mp4' AND NEW.type_id != 3 THEN			
			SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'При загрузке видео расширение файла должо быть .mp4';
		WHEN SUBSTRING(NEW.filename, -4) = '.jpg' AND NEW.type_id NOT IN(1, 2) THEN
			SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'При загрузке фото или обложки расширение файла должо быть .jpg';
		ELSE SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'В таблицу files загружаются только фото или видео';
END CASE;
END$$
DELIMITER ;


