/* Функция подсчитывающая средний рейтинг по фильмам и сериалам по киноличности на платформе */
DROP FUNCTION IF EXISTS rating_count;  
DELIMITER $$
CREATE FUNCTION rating_count(person_id BIGINT)
RETURNS DOUBLE DETERMINISTIC 
BEGIN
	DECLARE avg_rating DOUBLE;
	/*SET @avg_rating = 0*/
	SELECT ROUND(((AVG(film_ratings.rating) + AVG(serial_ratings.rating)) / 2), 2) INTO avg_rating 
	FROM film_ratings 
	JOIN films ON films.id = film_ratings.film_id 
	JOIN film_movie_persons ON film_movie_persons.film_id = films.id
	JOIN movie_persons ON movie_persons.id = film_movie_persons.movie_person_id 
	JOIN serial_movie_persons ON serial_movie_persons.movie_person_id = movie_persons.id
	JOIN serials ON serials.id = serial_movie_persons.serial_id 
	JOIN serial_ratings ON serial_ratings.serial_id = serials.id
	WHERE film_movie_persons.movie_person_id = person_id;
	RETURN avg_rating;
END$$
DELIMITER ;



