/* Страницы фильмов */
CREATE OR REPLACE VIEW films_view 
AS SELECT 
	films.id, 
	films.name AS 'Название', 
	films.year AS 'Год производства', 
	films.country AS 'Страна',
	GROUP_CONCAT(DISTINCT genre_types.type) AS 'Жанр',
	GROUP_CONCAT(DISTINCT (SELECT movie_persons.firstname FROM movie_persons WHERE movie_persons.id = film_movie_persons.movie_person_id AND movie_persons.type = 'director')) AS 'Режиссеры',
	GROUP_CONCAT(DISTINCT (SELECT movie_persons.firstname FROM movie_persons WHERE movie_persons.id = film_movie_persons.movie_person_id AND movie_persons.type = 'actor')) AS 'Актеры',
	CONCAT(films.box_office_world, ' $') AS 'Сборы в мире',
	CONCAT(films.box_office_rus, ' $') AS 'Сборы в России',
	films.premier_date_world AS 'Премьера в мире',
	films.premier_date_rus AS 'Премьера в России',
	CONCAT(films.age_limit, '+') AS 'Возраст',
	(SELECT AVG(rating) FROM film_ratings WHERE films.id = film_ratings.film_id) AS 'Рейтинг',
	CONCAT(films.duration, ' мин.') AS 'Продолжительность',
	(SELECT files.filename FROM files WHERE files.id = films.cover_file_id) AS 'Файл обложки',
	(SELECT files.filename FROM files WHERE files.id = films.film_file_id)AS 'Файл фильма'
FROM films 
JOIN film_genres ON film_genres.film_id = films.id  -- Для жанра
JOIN genre_types ON genre_types.id = film_genres.genre_type_id  -- Для жанра
JOIN film_movie_persons ON film_movie_persons.film_id = films.id -- Для режиссеров и актеров
JOIN movie_persons ON movie_persons.id = film_movie_persons.movie_person_id -- Для режиссеров и актеров
GROUP BY films.name
ORDER BY films.id;


/* Страницы сериалов */
CREATE OR REPLACE VIEW serials_view 
AS SELECT 
	serials.id,
	CONCAT(serials.name, ' (', (SELECT COUNT(*) FROM seasons_serials WHERE serial_id = serials.id), ' сезонов)') AS 'Название', 
	CONCAT((SELECT MIN(seasons_serials.year) FROM seasons_serials WHERE serial_id = serials.id), '-', 
		   (SELECT MAX(seasons_serials.year) FROM seasons_serials WHERE serial_id = serials.id)) AS 'Года',
	serials.country AS 'Страна',
	GROUP_CONCAT(DISTINCT genre_types.type) AS 'Жанр',
	GROUP_CONCAT(DISTINCT (SELECT movie_persons.firstname FROM movie_persons WHERE movie_persons.id = serial_movie_persons.movie_person_id AND movie_persons.type = 'director')) AS 'Режиссеры',
	GROUP_CONCAT(DISTINCT (SELECT movie_persons.firstname FROM movie_persons WHERE movie_persons.id = serial_movie_persons.movie_person_id AND movie_persons.type = 'actor')) AS 'Актеры',
	serials.premier_date_world AS 'Премьера в мире',
	serials.premier_date_rus AS 'Премьера в России',
	CONCAT(serials.age_limit, '+') AS 'Возраст',
	CONCAT((SELECT ROUND(AVG(episodes_serials.duration)) FROM episodes_serials WHERE episodes_serials.serial_id = serials.id), ' мин.') AS 'Продолжительность серии',
	(SELECT files.filename FROM files WHERE files.id = serials.cover_file_id) AS 'Файл обложки'
FROM serials
JOIN serial_genres ON serial_genres.serial_id = serials.id  -- Для жанра
JOIN genre_types ON genre_types.id = serial_genres.genre_type_id  -- Для жанра
JOIN serial_movie_persons ON serial_movie_persons.serial_id = serials.id -- Для режиссеров и актеров
JOIN movie_persons ON movie_persons.id = serial_movie_persons.movie_person_id -- Для режиссеров и актеров
GROUP BY serials.name
ORDER BY serials.id;


